function taxRateAmountCalc(price, tax_rate) {
    if(! tax_rate)
        var tax_rate = 0
    if(isNaN(price) || isNaN(tax_rate))
        return 0;
    else    
        return (( parseFloat(price) * parseFloat(tax_rate) )/100).toFixed(2);
}
