function calculateDueDate(inputDate, daysAfter) {
    const noOfDays = daysAfter ? parseInt(daysAfter) : 30;
    const startDate = new Date(inputDate);
    startDate.setDate(startDate.getDate() + noOfDays);
    if (startDate.getDate() <= 5) {
        startDate.setDate(5);
    } else if (startDate.getDate() <= 20) {
        startDate.setDate(20);
    } else {
        // if the month has 31 days than we have to use this
        startDate.setMonth(startDate.getMonth() + 1, 5);
        startDate.setDate(5);
    }
    return startDate;
}

function calculateAveragePercentage(percentages) {
    if(percentages.length ==0)
        return 0;
    var decimalValues = percentages.map(function(percentage) {
      return percentage / 100;
    });
  
    var sum = decimalValues.reduce(function(a, b) {
      return a + b;
    }, 0);
  
    var average = sum / decimalValues.length;
  
    return average * 100;
}

function exportTableToExcel(id, fileName, dynamicColumn = '', rmFixstring = ''){
    // var wb = XLSX.utils.table_to_book(document.getElementById(id), {sheet:"Sheet JS"});
    // var wbout = XLSX.write(wb, {bookType:'xlsx', bookSST:true, type: 'binary'});
    // function s2ab(s) {
    //                 var buf = new ArrayBuffer(s.length);
    //                 var view = new Uint8Array(buf);
    //                 for (var i=0; i<s.length; i++) view[i] = s.charCodeAt(i) & 0xFF;
    //                 return buf;
    // }
    // saveAs(new Blob([s2ab(wbout)],{type:"application/octet-stream"}), fileName+'.xlsx');

    var table = document.getElementById(id);
    var wb = XLSX.utils.book_new();
    var ws = XLSX.utils.table_to_sheet(table);

    // Apply number formatting to cells containing numbers
    var range = XLSX.utils.decode_range(ws['!ref']);
    for(var R = range.s.r; R <= range.e.r; ++R) {
        for(var C = range.s.c; C <= range.e.c; ++C) {
            var cell_address = {c:C, r:R};
            var cell_ref = XLSX.utils.encode_cell(cell_address);

            if (ws[cell_ref]) {
                var cellValue = ws[cell_ref].v;
                // Check if cell value is a string and contains '-staticstring-'
                if (typeof cellValue === 'string' && cellValue.includes('-staticstring-')) {
                    ws[cell_ref].t = 's'; // Set cell type to 's' for string
                    if (rmFixstring == 1) {
                        ws[cell_ref].v = removeFixStrongFn(cellValue);
                    }
                }
            }
            if(dynamicColumn){
                if(dynamicColumn.includes(C) && ws[cell_ref] && typeof ws[cell_ref].v === 'number') {
                    // ws[cell_ref].z = "#,##0.00"; // Format numbers with comma separator
                    ws[cell_ref].t = 's'; // Set cell type to 's' for string
                    ws[cell_ref].v = numberWithCommas(ws[cell_ref].v); // Format number with comma separators
                }
            }else{
                if(ws[cell_ref] && typeof ws[cell_ref].v === 'number') {
                    // ws[cell_ref].z = "#,##0.00"; // Format numbers with comma separator
                    ws[cell_ref].t = 's'; // Set cell type to 's' for string
                    ws[cell_ref].v = numberWithCommas(ws[cell_ref].v); // Format number with comma separators
                }
            }
        }
    }
    XLSX.utils.book_append_sheet(wb, ws, "Sheet JS");
    var wbout = XLSX.write(wb, {bookType:'xlsx', bookSST:true, type: 'binary'});
    function s2ab(s) {
        var buf = new ArrayBuffer(s.length);
        var view = new Uint8Array(buf);
        for (var i=0; i<s.length; i++) view[i] = s.charCodeAt(i) & 0xFF;
        return buf;
    }
    function numberWithCommas(x) {
        return x.toString().replace('.',",");
        // return temp.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    }
    function removeFixStrongFn(x){
        // to avoid issue of price/custom number column it automatically removes comma from string
        return x.toString().replace('-staticstring-',""); 
    }
    saveAs(new Blob([s2ab(wbout)], {type:"application/octet-stream"}), fileName + '.xlsx');
}

